// src/Pages/LoginPage.jsx
import { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function LoginPage() {
  const [username, setUsername] = useState("");
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    if (username.trim()) {
      localStorage.setItem("username", username);
      navigate("/dashboard");
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-black text-white">
      <form onSubmit={handleLogin} className="bg-gray-900 p-6 rounded-lg w-">
        <h2 className="text-5xl mb-4 font-bold text-center">Login</h2>
        <input
          className="w-full p-2 mb-4 rounded bg-gray-800"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <button className="w-full bg-cyan-500 hover:bg-cyan-600 p-2 rounded" type="submit">
          Log In
        </button>
        <p className="text-sm mt-4 text-center">
          No account? <a className="text-cyan-400" href="/register">Register</a>
        </p>
      </form>
    </div>
  );
}
