import React from "react";
import { Card, CardContent } from "./ui/Card";
import {
  LineChart, Line, XAxis, YAxis, Tooltip, ResponsiveContainer, CartesianGrid, Area
} from "recharts";

const fillLevelData = [
  { time: "Week 1", level: 60 },
  { time: "Week 2", level: 25 },
  { time: "Week 3", level: 90 },
  { time: "Week 4", level: 65 },
  { time: "Week 5", level: 30 },
  { time: "Week 6", level: 85 },
  { time: "Week 7", level: 100 },
];

export function FillLevelChart() {
  return (
    <Card>
      <CardContent className="p-4">
        <h2 className="text-xl font-bold text-cyan-400 mb-4">Fill Level Over Time</h2>
        <ResponsiveContainer width="100%" height={250}>
          <LineChart data={fillLevelData}>
            <defs>
              <linearGradient id="levelGradient" x1="0" y1="0" x2="0" y2="1">
                <stop offset="0%" stopColor="#06b6d4" stopOpacity={0.8} />
                <stop offset="100%" stopColor="#06b6d4" stopOpacity={0} />
              </linearGradient>
            </defs>
            <CartesianGrid strokeDasharray="3 3" stroke="#444" />
            <XAxis dataKey="time" stroke="#ccc" />
            <YAxis stroke="#ccc" domain={[0, 100]} />
            <Tooltip
              contentStyle={{ backgroundColor: "#1f2937", border: "none", borderRadius: "8px" }}
              labelStyle={{ color: "#a5f3fc" }}
              cursor={{ stroke: "#06b6d4", strokeWidth: 2 }}
            />
            <Area
              type="monotone"
              dataKey="level"
              stroke="#06b6d4"
              fill="url(#levelGradient)"
              strokeWidth={2}
              dot={{ r: 4, stroke: "#06b6d4", fill: "#06b6d4" }}
              activeDot={{ r: 6 }}
            />
          </LineChart>
        </ResponsiveContainer>
      </CardContent>
    </Card>
  );
}
