import { useState } from "react";

export default function ProfileSettingsPage() {
  // Initialize state with mock data
  const [user, setUser] = useState({
    fullName: "Jane Doe",
    contactEmail: "jane.doe@example.com",
    phone: "+1 555-123-4567",
    language: "English",
    notifications: true,
  });

  const [password, setPassword] = useState("");

  // No loading state needed for mock data

  return (
    <div className="max-w-4xl mx-auto p-8 bg-gray-900 rounded-xl text-white min-h-screen">
      <h2 className="text-5xl font-extrabold mb-8 text-cyan-400">Profile Settings</h2>

      {/* Your form here, with inputs using `user` state */}

      {/* Example input */}
      <label htmlFor="fullName" className="block mb-1 font-medium">
        Full Name
      </label>
      <input
        id="fullName"
        type="text"
        value={user.fullName}
        onChange={(e) => setUser({ ...user, fullName: e.target.value })}
        className="w-full bg-gray-800 p-3 rounded mb-6"
      />

      {/* Rest of your inputs ... */}
    </div>
  );
}
